import React from 'react';

class ScrollButton extends React.Component {
  scrollToSection = () => {
    const section = document.getElementById('targetSection');
    window.scrollTo({
      top: section.offsetTop,
      behavior: 'smooth'
    });
  };

  render() {
    return (
      <button onClick={this.scrollToSection}>
        Scroll to Section
      </button>
    );
  }
}

export default ScrollButton;
