import React from 'react'
import { useSelector } from 'react-redux'
import CostComparison from './CostComparison'
import HowTo from './HowTo'
import { DATA200, DATA200T, NDATA200, NDATA200T } from '../../Data';
import Products from './Products';
import Buttons from './Buttons';
import ScrollDownButton from './ScrollDownButton';
import ScrollToTop from "react-scroll-to-top";
import { Dropdown } from 'react-bootstrap';




function Landing() {

    const sqft = useSelector((state) => state.squareft.value);
    const condition = useSelector((state) => state.conditioner.value);

    // console.log(condition);

    const scrollToBottom = () => {
        window.scrollTo({
          top: document.documentElement.scrollHeight,
          behavior: 'smooth',
        });
      };


    return (
        <>
          <ScrollToTop color="#0070c0" />
            <div className="container calculator bg-light-subtle">
                <div className="row">
                    <div className="col">
                        <h2 className="text-danger fw-bold mt-3 text-center">Custom Pontoon Protection Kit <br />
                            for New Pontoons up to {Math.round(sqft)} Sq. Ft.</h2>
                    </div>
               <Dropdown>
                  <Dropdown.Toggle variant="secondary" id="dropdown-benefits">
                    Top 7 Benefits of Protecting Your New Pontoons with Alumetron and VS721
                  </Dropdown.Toggle>

                  <Dropdown.Menu>
                    <Dropdown.Item>Pontoons Stay Clean and Shiny Year After Year</Dropdown.Item>
                    <Dropdown.Item>Pride of Ownership. Your Boat is a Reflection of Your Personality.</Dropdown.Item>
                    <Dropdown.Item>3 Year Transferable Factory Warranty.</Dropdown.Item>
                    <Dropdown.Item>Greater Trade or Resale Value. Adding $5,000 to $10,000 is Common.</Dropdown.Item>
                    <Dropdown.Item>Better Fuel Economy. Reduce Fuel Consumption Up to 20%.</Dropdown.Item>
                    <Dropdown.Item>Easier Maintenance. Pontoons Can Stay Fouling Free All Summer.</Dropdown.Item>
                    <Dropdown.Item>Lasts up to 10 Years. Save Money by Reducing Costly Annual Maintenance.</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
                    <div className="text-end d-flex align-items-center justify-content-end" onClick={scrollToBottom} >
                     <button type="button" className="btn btn-warning me-4 mb-1">
                        <i className="cursor-pointer bi bi-arrow-down-circle-fill fs-4 me-2"></i>
                        <span className="fs-5 me-3 cursor-pointer">Scroll Down</span>
                    </button>

                    </div>
                </div>
                <Products />
                <Buttons />
                <div className="text-end d-flex align-items-center justify-content-end" onClick={scrollToBottom} >
                     <button type="button" className="btn btn-light me-4 mt-3">
                        <i className="cursor-pointer bi bi-arrow-down-circle-fill fs-4 me-2"></i>
                        <span className="fs-5 me-3 cursor-pointer">Scroll Down</span>
                    </button>

                    </div>
                <CostComparison />
              
                <HowTo />
            </div>
        </>
    )
}

export default Landing
